INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (1, 2010, 'Deitel', 8, 'Prentice Hall', 10, 12, '9788576055631', 312, 'A oitava edição de Java - como programar, lançada pela Pearson Education, chega ao mercado com novo design e um traço inovador.', 'Java - Como Programar');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (2, 2013, 'Ricardo Lecheta', 3, 'Novatec', 30, 12, '9788575223444', 123, 'O Android é a plataforma open-source criada pelo Google para o desenvolvimento de aplicações para dispositivos móveis. É a nova sensação do momento e está revolucionando o desenvolvimento de aplicações.', 'Google Android');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (3, 2005, 'Sierra Kathy', 1, 'Alta Books', 5, 12, '9788576081739', 139, 'Use a Cabeça Java é uma experiência completa de aprendizado em programação orientada a objetos (OO) e Java.', 'Use a Cabeça Java');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (4, 2011, 'Andrew S. Tanenbaum', 5, 'Pearson Education', 50, 12, '9788576059240', 170, 'Este livro aborda as redes desenvolvidas a partir de 1990, explicando com elas funcionam internamente. Andrew Tanenbaum apresenta explicações detalhadas e exemplos baseados na Internet para facilitar a compreensão do leitor.', 'Redes de Computadores');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (5, 2006, 'Deitel', 5, 'Prentice Hall', 10, 12, '8576050560', 410, 'A melhor e mais respeitada introdução a C++, programação orientada a objetos (POO) e projeto orientado a objetos (OOD) com UML 2 utilizando o Deitel Live-Code.', 'C ++ Como Programar');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (6, 2013, 'Dan Brown', 1, 'Arqueiro', 20, 13, '9788580411522', 39.90, 'Neste novo e fascinante thriller Dan Brown retoma a mistura magistral de história, arte, códigos e símbolos que o consagrou em O código Da Vinci, Anjos e demônios e O símbolo perdido e faz de Inferno sua aposta mais alta até o momento.', 'Inferno');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (7, 2004, 'Dan Brown', 1, 'Arqueiro', 20, 13, '8575421131', 23.90, 'Um assassinato dentro do Museu do Louvre, em Paris, traz à tona uma sinistra conspiração para revelar um segredo que foi protegido por uma sociedade secreta desde os tempos de Jesus Cristo.' , 'O Código da Vinci');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (8, 2004, 'Dan Brown', 1, 'Arqueiro', 1, 13, '8575421468', 23.90, 'Antes de decifrar O Código Da Vinci, Robert Langdon, o famoso professor de simbologia de Harvard, vive sua primeira aventura em Anjos e Demônios, quando tenta impedir que uma antiga sociedade secreta destrua a Cidade do Vaticano.', 'Anjos e Demônios');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (9, 2007, 'Mario Puzo', 1, 'Bestbolso', 2, 13, '8577990192', 25, 'A mais perfeita reconstituição da Cosa Nostra, a Máfia americana, e do mundo alucinante criado por cinco famílias do mafiosi em guerra em Nova York.', 'O Poderoso Chefão');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (10, 2005, 'Bruna Surfistinha', 1, 'Panda Books', 60, 15, '9788576950172', 27.10, 'Neste livro você vai conhecer detalhes reveladores da menina de classe média alta que trocou os finais de semana com a família no Guarujá para se prostituir aos 17 anos.', 'O Doce Veneno do Escorpião');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (11, 2009, 'C. S. Lewis', 2, 'Martins Fontes', 10, 13, '9788578270698', 29.9, 'Narnia - onde um dragão desperta, onde as estrelas andam na terra e onde tudo pode acontecer!\n\n50 anos após a primeira aparição das Crônicas de Nárnia, as fabulosas histórias do país do Leão Aslan também fazem parte da biblioteca dos adultos.', 'As Crônicas de Nárnia - Volume Único');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (12, 2010, 'Nicholas Sparks', 1, 'Novo Conceito', 5, 13, '9788563219077', 21.8999999999999986, 'Aos 17 anos, Verônica Miller, ou simplesmente Ronnie, vê sua vida virar de cabeça para baixo quando seus pais se divorciam e seu pai decide se mudar para a praia de Wrightsville, na Carolina do Norte.', 'A Última Música');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (13, 2011, 'Nicholas Sparks', 1, 'Novo Conceito', 7, 13, '9788563219268', 15.2100000000000009, 'Cada mês de abril, quando o vento sopra do mar e se mistura com o perfume de violetas, Landon Carter recorda seu último ano na High Beaufort. Isso era 1958 e Landon já tinha namorado uma ou duas meninas. Ele sempre jurou que já tinha se apaixonado antes.', 'Um Amor Para Recordar');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (14, 2007, 'Markus Zusak', 1, 'Intrinseca', 50, 13, '9788598078175', 26.8999999999999986, 'Entre 1939 e 1943, Liesel Meminger encontrou a Morte três vezes.', 'A Menina que Roubava Livros');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (15, 2008, 'William P. Young', 1, 'Sextante', 2, 13, '9788599296363', 14.9000000000000004, 'Durante uma viagem de fim de semana, a filha mais nova de Mack Allen Phillips é raptada e evidências de que ela foi brutalmente assassinada são encontradas numa cabana abandonada.', 'A Cabana');
INSERT INTO livro (id, ano, autor, edicao, editora, estoque, genero, isbn, preco, sinopse, titulo) VALUES (16, 2009, 'Stephenie Meyer', 1, 'Intrinseca', 1, 13, '9788598078765', 39.8999999999999986, 'Crepúsculo desafiou a imaginação. Lua nova deixou os leitores sedentos por mais. Eclipse transformou a série em um fenômeno global. Agora, Amanhecer!', 'Amanhecer');
